points = FileAttachment("data/points.json").json()

speciesList = ["All", ...new Set(points.map(d =&gt; d.species))]
regionList  = ["All", ...new Set(points.map(d =&gt; d.region))]
seasonList  = ["All", ...new Set(points.map(d =&gt; d.season))]

viewof species = Inputs.select(speciesList, {label: "Species"})
viewof region = Inputs.select(regionList, {label: "Area"})
viewof season = Inputs.select(seasonList, {label: "Period"})

filtered = points.filter(p =&gt;
  (species === "All" || p.species === species) &&
  (region  === "All" || p.region  === region) &&
  (season  === "All" || p.season  === season)
)

map = {
  const div = html`&lt;div style="width:100%; height:70vh; margin-top:20px; border-radius:10px; overflow:hidden;"&gt;&lt;/div&gt;`;
  const map = L.map(div).setView([20, 0], 2);

  L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
    maxZoom: 19,
    attribution: "© OpenStreetMap"
  }).addTo(map);

  filtered.forEach(p =&gt; {
    const popup = `
      &lt;div style="display:flex; gap:10px; align-items:center; max-width:260px;"&gt;
        &lt;img src="${p.photo}" width="50" height="50" style="border-radius:6px;" /&gt;
        &lt;div&gt;
          &lt;strong&gt;${p.commonName}&lt;/strong&gt;&lt;br&gt;
          &lt;em&gt;${p.species}&lt;/em&gt;&lt;br&gt;
          &lt;small&gt;${p.region} — ${p.season}&lt;/small&gt;&lt;br&gt;
          &lt;small&gt;Temp: ${p.temp}°C — Salinity: ${p.salinity}‰&lt;/small&gt;&lt;br&gt;
          ${p.link}
        &lt;/div&gt;
      &lt;/div&gt;
    `;
    L.marker([p.lat, p.lng]).addTo(map).bindPopup(popup);
  });

  return div;
}

